    private void BtnCalActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_BtnCalActionPerformed
        double precio_base;
double precio_instal=0; 
double precio_for=0; 
double precio_ali=0; 
 

 
precio_base = Double.parseDouble(TxtPrecioBase.getText());
precio_instal = Double.parseDouble(etiIsta.getText());
precio_for = Double.parseDouble(etiFor.getText());
precio_ali = Double.parseDouble(etiAli.getText());


 
double precio_total;
 
precio_total = precio_base;
 
if (TbtnIstalacion.isSelected()) { 
 precio_total = precio_total+precio_instal;
}
 
if (TbtnAlimentacionBD.isSelected()) { 
 precio_total = precio_total+precio_for; 
}
 
if (TbtnFormacion.isSelected()) {
 precio_total = precio_total+precio_ali;
}
 
 etiTotal.setText(precio_total+" â‚¬");

    }//GEN-LAST:event_BtnCalActionPerformed

